{/* ====================== üîç Search Bar ====================== */}
<form className="search-bar card" onSubmit={onSearch}>
  <div className="filters-row search-centered">
    <label>
      ‡πÇ‡∏´‡∏°‡∏î‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤
      <select
        value={mode}
        onChange={(e) => {
          setMode(e.target.value);
          setErr((x) => ({ ...x, search: "" }));
          setOpenSugs(false);
        }}
      >
        <option value="keyword">‡∏Ñ‡∏µ‡∏¢‡πå‡πÄ‡∏ß‡∏¥‡∏£‡πå‡∏î</option>
        <option value="category">‡∏´‡∏°‡∏ß‡∏î‡∏´‡∏°‡∏π‡πà</option>
        <option value="subcategory">‡∏´‡∏°‡∏ß‡∏î‡∏´‡∏°‡∏π‡πà‡∏¢‡πà‡∏≠‡∏¢</option>
      </select>
    </label>

    {/* ‡∏ä‡πà‡∏≠‡∏á‡∏Å‡∏£‡∏≠‡∏Å‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤ */}
    <label className="grow" ref={sugWrapRef}>
      <input
        ref={inputRef}
        type="text"
        className="search-input"
        placeholder="üîç ‡∏û‡∏¥‡∏°‡∏û‡πå‡∏ä‡∏∑‡πà‡∏≠‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤ ‡πÄ‡∏ä‡πà‡∏ô ‡∏Å‡∏∏‡∏´‡∏•‡∏≤‡∏ö ‡∏Å‡∏£‡∏∞‡∏ö‡∏≠‡∏á‡πÄ‡∏û‡∏ä‡∏£ ‡∏î‡∏¥‡∏ô‡∏õ‡∏•‡∏π‡∏Å..."
        value={keyword}
        onChange={(e) => setKeyword(e.target.value)}
        onFocus={() => sugs.length && setOpenSugs(true)}
        onBlur={() => setTimeout(() => setOpenSugs(false), 120)}
        onKeyDown={onKeyDown}
      />

      {/* ‡∏Å‡∏•‡πà‡∏≠‡∏á‡πÅ‡∏ô‡∏∞‡∏ô‡∏≥ (Live Suggest) */}
      {openSugs && (
        <div className="live-suggest" ref={sugListRef} onScroll={onSugScroll}>
          {sugs.map((p, idx) => (
            <button
              key={p.id}
              data-idx={idx}
              type="button"
              className={`live-suggest-item${
                idx === sugActive ? " active" : ""
              }`}
              onMouseEnter={() => setSugActive(idx)}
              onClick={() => pickSuggest(p)}
            >
              <span className="s-name">{p.name}</span>
              <span className="s-price">
                {p.price ? `‡∏ø${p.price.toLocaleString()}` : "‚Äî"}
              </span>
            </button>
          ))}
          {sugLoading && (
            <div className="live-suggest-loading">‡∏Å‡∏≥‡∏•‡∏±‡∏á‡πÇ‡∏´‡∏•‡∏î‚Ä¶</div>
          )}
          {!sugHasMore && !sugs.length && (
            <div className="live-suggest-empty">‡πÑ‡∏°‡πà‡∏û‡∏ö‡∏Ñ‡∏≥‡∏ó‡∏µ‡πà‡πÉ‡∏Å‡∏•‡πâ‡πÄ‡∏Ñ‡∏µ‡∏¢‡∏á</div>
          )}
        </div>
      )}
    </label>

    {/* ‡∏õ‡∏∏‡πà‡∏°‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤‡πÉ‡∏´‡∏°‡πà (‡∏Ç‡∏ô‡∏≤‡∏î‡∏û‡∏≠‡∏î‡∏µ ‡∏ï‡∏£‡∏á‡∏Å‡∏•‡∏≤‡∏á ‡∏™‡∏ß‡∏¢‡∏Ñ‡∏•‡∏µ‡∏ô) */}
    <button
      className="btn-search improved"
      type="submit"
      disabled={loading.search}
    >
      {loading.search ? "üîé ‡∏Å‡∏≥‡∏•‡∏±‡∏á‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤..." : "‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤"}
    </button>
  </div>

  {!!err.search && <div className="info-inline warn">{err.search}</div>}
</form>
