// src/admin/VariantsManager.js
// ======================================================================
// จัดการตัวเลือก (Options/Values) และ Variants ของสินค้า — ฉบับ "รายละเอียดที่ N"
// - ไม่โชว์ชื่อจริงของออปชั่น (เช่น สี/ขนาด) ทั้งในฟอร์ม และข้อความคอมโบ
// - เพิ่มปุ่ม "+ เพิ่มรายละเอียด" เพื่อสร้างออปชั่นเพิ่มได้ไม่จำกัด
// ใช้กับ backend เส้นทางเดิมทั้งหมด:
//   GET  /api/variants/products/:product_id/options
//   POST /api/variants/products/:product_id/options
//   PUT  /api/variants/options/:option_id
//   DELETE /api/variants/options/:option_id
//   POST /api/variants/options/:option_id/values
//   DELETE /api/variants/values/:value_id
//   GET  /api/variants/product/:product_id?active=1
//   POST /api/variants/products/:product_id/variants
//   PUT  /api/variants/:variant_id
//   DELETE /api/variants/:variant_id
//   POST /api/variants/products/:product_id/variants/generate
// ======================================================================

import React, { useEffect, useMemo, useRef, useState } from "react";
import { useParams, Link } from "react-router-dom";
import axios from "axios";

// JWT แอดมินจาก localStorage
const authHeader = () => {
  const t = localStorage.getItem("token");
  return t ? { Authorization: `Bearer ${t}` } : {};
};

// helper อัปโหลดไฟล์ไป /api/uploads (ถ้ามี)
async function tryUpload(file) {
  const fd = new FormData();
  fd.append("file", file);
  const { data } = await axios.post("/api/uploads", fd, {
    headers: { ...authHeader(), "Content-Type": "multipart/form-data" },
  });
  if (typeof data === "string") return data;
  return data?.url || data?.Location || data?.path || null;
}

export default function VariantsManager() {
  const params = useParams();
  const productId = Number(params.productId || params.id);

  const [loading, setLoading] = useState(true);
  const [busy, setBusy] = useState(false);
  const [error, setError] = useState("");
  const [msg, setMsg] = useState("");

  // ข้อมูลหลัก
  const [product, setProduct] = useState(null);
  const [options, setOptions] = useState([]);  // [{option_id, option_name, values:[{value_id,value_name}]}]
  const [variants, setVariants] = useState([]); // [{... , combo/combos:[{option_id,value_id}]}]

  // ฟอร์มสร้าง variant
  const [draft, setDraft] = useState({
    sku: "",
    price: "",
    stock_qty: "",
    image_url: "",
    values: {},     // mapping { [option_id]: value_id }
    textInputs: {}, // เก็บข้อความที่พิมพ์ต่อออปชั่น
  });

  // generate combos
  const [skuPrefix, setSkuPrefix] = useState(`P${productId}`);
  const [genPrice, setGenPrice] = useState(0);
  const [genStock, setGenStock] = useState(0);

  const fileInputRef = useRef(null);

  const boom = (e, fallback = "เกิดข้อผิดพลาด") => {
    setError(e?.response?.data?.message || e?.response?.data?.error || e?.message || fallback);
    console.error(fallback, e);
  };

  // โหลดข้อมูล
  async function load() {
    setLoading(true);
    setError(""); setMsg("");
    try {
      const p = await axios.get(`/api/products/${productId}`).catch(() => ({ data: null }));
      setProduct(p?.data || null);

      const [{ data: opts }, { data: vars }] = await Promise.all([
        axios.get(`/api/variants/products/${productId}/options`),
        axios.get(`/api/variants/product/${productId}?active=1`),
      ]);
      setOptions(opts || []);
      setVariants(vars || []);
    } catch (e) {
      boom(e, "โหลดข้อมูลไม่สำเร็จ");
    } finally {
      setLoading(false);
    }
  }

  useEffect(() => { load(); /* eslint-disable-next-line */ }, [productId]);

  // ===== helpers: label/lookup =====
  // index ของ option → ใช้ตั้งป้าย "รายละเอียดที่ N"
  const optIndexById = useMemo(() => {
    const m = new Map();
    options.forEach((o, i) => m.set(o.option_id, i));
    return m;
  }, [options]);

  const valNameById = useMemo(() => {
    const m = new Map();
    options.forEach((o) => (o.values || []).forEach((v) => m.set(v.value_id, v.value_name)));
    return m;
  }, [options]);

  // ป้ายอินพุต — โชว์เฉพาะ generic เท่านั้น
  const formLabel = (_opt, idx) => `รายละเอียดที่ ${idx + 1}`;

  // ข้อความคอมโบในตาราง — โชว์เฉพาะ "รายละเอียดที่ N: ค่า"
  const comboText = (row) => {
    const list = row.combo || row.combos || [];
    if (!list.length) return "—";
    return list.map((c) => {
      const idx = optIndexById.get(c.option_id) ?? 0;
      const label = `รายละเอียดที่ ${idx + 1}`;
      return `${label}: ${valNameById.get(c.value_id) ?? c.value_id}`;
    }).join(" ・ ");
  };

  // ===== actions: OPTIONS =====
  async function addOption(name) {
    setBusy(true);
    try {
      await axios.post(
        `/api/variants/products/${productId}/options`,
        { option_name: (name || "").trim() || undefined },
        { headers: authHeader() }
      );
      await load();
      setMsg("เพิ่มรายละเอียดสำเร็จ");
    } catch (e) {
      boom(e, "เพิ่มรายละเอียดไม่สำเร็จ");
    } finally {
      setBusy(false);
    }
  }

  async function renameOption(option_id, name) {
    setBusy(true);
    try {
      await axios.put(
        `/api/variants/options/${option_id}`,
        { option_name: name },
        { headers: authHeader() }
      );
      await load();
      setMsg("แก้ชื่อรายละเอียดสำเร็จ");
    } catch (e) {
      boom(e, "แก้ชื่อรายละเอียดไม่สำเร็จ");
    } finally {
      setBusy(false);
    }
  }

  async function deleteOption(option_id) {
    if (!window.confirm("ลบรายละเอียดนี้ทั้งหมด?")) return;
    setBusy(true);
    try {
      await axios.delete(`/api/variants/options/${option_id}`, { headers: authHeader() });
      await load();
      setMsg("ลบรายละเอียดสำเร็จ");
    } catch (e) {
      boom(e, "ลบรายละเอียดไม่สำเร็จ");
    } finally {
      setBusy(false);
    }
  }

  // ===== actions: VALUES =====
  async function addValue(option_id, value_name) {
    setBusy(true);
    try {
      await axios.post(`/api/variants/options/${option_id}/values`, { value_name }, { headers: authHeader() });
      await load();
      setMsg(`เพิ่มค่า "${value_name}" สำเร็จ`);
    } catch (e) {
      boom(e, "เพิ่มค่าไม่สำเร็จ");
    } finally {
      setBusy(false);
    }
  }

  async function deleteValue(value_id) {
    if (!window.confirm("ลบค่านี้?")) return;
    setBusy(true);
    try {
      await axios.delete(`/api/variants/values/${value_id}`, { headers: authHeader() });
      await load();
      setMsg("ลบค่าสำเร็จ");
    } catch (e) {
      boom(e, "ลบค่าไม่สำเร็จ (อาจถูกใช้อยู่ใน variant)");
    } finally {
      setBusy(false);
    }
  }

  // UX: เลือกค่าที่พิมพ์ / สร้างใหม่ถ้าไม่มี
  async function chooseOrCreateValue(option, typed) {
    const text = (typed || "").trim();
    const oid = option.option_id;
    setDraft((s) => ({ ...s, textInputs: { ...s.textInputs, [oid]: text } }));

    if (!text) {
      setDraft((s) => ({ ...s, values: { ...s.values, [oid]: "" } }));
      return;
    }

    const exists = (option.values || []).find(
      (v) => (v.value_name || "").toLowerCase() === text.toLowerCase()
    );
    if (exists) {
      setDraft((s) => ({ ...s, values: { ...s.values, [oid]: exists.value_id } }));
      return;
    }

    await addValue(oid, text);
    const found = [...valNameById.entries()].find(
      ([, name]) => (name || "").toLowerCase() === text.toLowerCase()
    );
    if (found) {
      const value_id = found[0];
      setDraft((s) => ({
        ...s,
        values: { ...s.values, [oid]: value_id },
        textInputs: { ...s.textInputs, [oid]: text },
      }));
    }
  }

  // ===== actions: VARIANTS =====
  async function createVariant() {
    setBusy(true);
    setError(""); setMsg("");
    try {
      const option_values = Object.entries(draft.values)
        .map(([oid, vid]) => ({ option_id: Number(oid), value_id: Number(vid) }))
        .filter((v) => v.value_id);

      await axios.post(
        `/api/variants/products/${productId}/variants`,
        {
          sku: (draft.sku || "").trim() || null,
          price: draft.price === "" ? 0 : Math.max(0, Number(draft.price || 0)),
          stock_qty: Math.max(0, Number(draft.stock_qty || 0)),
          image_url: (draft.image_url || "").trim() || null,
          is_active: true,
          option_values,
        },
        { headers: authHeader() }
      );

      setDraft({ sku: "", price: "", stock_qty: "", image_url: "", values: {}, textInputs: {} });
      await load();
      setMsg("บันทึก Variant สำเร็จ");
    } catch (e) {
      boom(e, "บันทึก Variant ไม่สำเร็จ");
    } finally {
      setBusy(false);
    }
  }

  async function updateVariant(variant_id, patch) {
    setBusy(true);
    setError(""); setMsg("");
    try {
      const body = {};
      if ("price" in patch) body.price = patch.price === "" ? null : Math.max(0, Number(patch.price || 0));
      if ("stock_qty" in patch) body.stock_qty = Math.max(0, Number(patch.stock_qty || 0));
      if ("sku" in patch) body.sku = patch.sku;
      if ("is_active" in patch) body.is_active = !!patch.is_active;
      if ("image_url" in patch) body.image_url = (patch.image_url || "").trim() || null;

      await axios.put(`/api/variants/${variant_id}`, body, { headers: authHeader() });
      await load();
      setMsg("อัปเดต Variant สำเร็จ");
    } catch (e) {
      boom(e, "อัปเดต Variant ไม่สำเร็จ");
    } finally {
      setBusy(false);
    }
  }

  async function deleteVariant(variant_id) {
    if (!window.confirm("ลบ Variant นี้?")) return;
    setBusy(true);
    try {
      await axios.delete(`/api/variants/${variant_id}`, { headers: authHeader() });
      await load();
      setMsg("ลบ Variant สำเร็จ");
    } catch (e) {
      boom(e, "ลบ Variant ไม่สำเร็จ");
    } finally {
      setBusy(false);
    }
  }

  async function generateCombos() {
    setBusy(true);
    setError(""); setMsg("");
    try {
      await axios.post(
        `/api/variants/products/${productId}/variants/generate`,
        {
          sku_prefix: (skuPrefix || "").trim() || `P${productId}`,
          price: Math.max(0, Number(genPrice || 0)),
          stock_qty: Math.max(0, Number(genStock || 0)),
        },
        { headers: authHeader() }
      );
      await load();
      setMsg("สร้างคอมบิเนชันสำเร็จ");
    } catch (e) {
      boom(e, "สร้างคอมบิเนชันไม่สำเร็จ");
    } finally {
      setBusy(false);
    }
  }

  if (loading) return <div style={{ padding: 16 }}>กำลังโหลด...</div>;

  return (
    <div style={{ padding: 16, display: "grid", gap: 16 }}>
      {/* Header */}
      <div style={{ display: "flex", alignItems: "center", gap: 10 }}>
        <h2 style={{ margin: 0 }}>
          ตัวเลือก/Variants —{" "}
          {product?.product_name ? `${product.product_name} (#${productId})` : `สินค้า #${productId}`}
        </h2>

        {/* ปุ่มแสดง/ซ่อนรายการรายละเอียด (ออปชั่น) */}
        <button style={{ marginLeft: 12 }} onClick={() => setShowOptions((s) => !s)}>
          {showOptions ? "ซ่อนรายการรายละเอียด" : "แสดงรายการรายละเอียด"}
        </button>

        <Link to="/admin" style={{ marginLeft: "auto" }}>← กลับแอดมิน</Link>
      </div>

      {/* สถานะข้อความ */}
      {(error || msg) && (
        <div style={{ display: "flex", gap: 12, flexWrap: "wrap" }}>
          {error && <div style={{ color:"#fff", background:"#d33", padding:"8px 12px", borderRadius:8 }}>{error}</div>}
          {msg   && <div style={{ color:"#fff", background:"green", padding:"8px 12px", borderRadius:8 }}>{msg}</div>}
        </div>
      )}

      {/* เพิ่ม Variant เดี่ยว */}
      <section style={{ border: "1px solid #eee", borderRadius: 12, padding: 12 }}>
        <h3>เพิ่มแบบ/ตัวเลือกสินค้า (Variant)</h3>

        <div style={{ display: "grid", gap: 10, maxWidth: 720 }}>
          <input
            placeholder="SKU (ถ้ามี)"
            value={draft.sku}
            onChange={(e) => setDraft((s) => ({ ...s, sku: e.target.value }))}
            disabled={busy}
          />

          {/* รูป Variant */}
          <div style={{ display: "grid", gap: 6 }}>
            <label style={{ fontWeight: 600 }}>รูปภาพของแบบ/ตัวเลือก (URL)</label>
            <div style={{ display: "flex", gap: 8, alignItems: "center" }}>
              <input
                placeholder="https:// ตัวอย่างรูปสินค้าเฉพาะแบบ/ตัวเลือก"
                value={draft.image_url}
                onChange={(e) => setDraft((s) => ({ ...s, image_url: e.target.value }))}
                disabled={busy}
                style={{ flex: 1 }}
              />
              <input
                type="file"
                accept="image/*"
                ref={fileInputRef}
                style={{ display: "none" }}
                onChange={async (e) => {
                  const f = e.target.files?.[0];
                  if (!f) return;
                  setBusy(true);
                  try {
                    const url = await tryUpload(f);
                    if (!url) throw new Error("อัปโหลดไม่สำเร็จหรือไม่ได้คืน URL");
                    setDraft((s) => ({ ...s, image_url: url }));
                    setMsg("อัปโหลดรูปสำเร็จ");
                  } catch (err) {
                    boom(err, "อัปโหลดรูปไม่สำเร็จ (ถ้าไม่มี /api/uploads ให้วาง URL รูปแทน)");
                  } finally {
                    setBusy(false);
                    if (fileInputRef.current) fileInputRef.current.value = "";
                  }
                }}
              />
              <button type="button" onClick={() => fileInputRef.current?.click()} disabled={busy}>
                เลือกไฟล์…
              </button>
            </div>
          </div>
        </div>

        {/* แถว "รายละเอียดที่ N" + ปุ่มเพิ่มรายละเอียด */}
        <div style={{ marginTop: 12, display: "grid", gap: 12 }}>
          <div style={{ display: "flex", gap: 8, alignItems: "center", flexWrap: "wrap" }}>
            <strong>รายละเอียดของสินค้า</strong>
            <button
              onClick={() => addOption("")}
              disabled={busy}
              title="เพิ่มรายละเอียด (เช่น แบบซอง / ขนาด / น้ำหนัก ฯลฯ)"
              style={{ padding: "6px 10px" }}
            >
              + เพิ่มรายละเอียด
            </button>
            {options.length === 0 && <span style={{ color: "#777" }}>ยังไม่มีรายละเอียด – กดปุ่ม “+ เพิ่มรายละเอียด”</span>}
          </div>

          {options.map((opt, idx) => (
            <div key={opt.option_id} style={{ display: "grid", gap: 6 }}>
              <label style={{ fontWeight: 600 }}>{formLabel(opt, idx)}</label>
              <input
                list={`opt-${opt.option_id}-list`}
                placeholder="พิมพ์ค่า เช่น 'แบบซอง', '500 กรัม', 'สีแดง' หรือเลือกจากลิสต์ แล้วกด Enter/คลิกออก"
                value={draft.textInputs[opt.option_id] ?? ""}
                onChange={(e) =>
                  setDraft((s) => ({
                    ...s,
                    textInputs: { ...s.textInputs, [opt.option_id]: e.target.value },
                  }))
                }
                onBlur={(e) => chooseOrCreateValue(opt, e.target.value)}
                onKeyDown={(e) => {
                  if (e.key === "Enter") {
                    e.preventDefault();
                    chooseOrCreateValue(opt, e.currentTarget.value);
                  }
                }}
                disabled={busy}
              />
              <datalist id={`opt-${opt.option_id}-list`}>
                {(opt.values || []).map((v) => (
                  <option key={v.value_id} value={v.value_name} />
                ))}
              </datalist>
              <small style={{ color: "#666" }}>
                ปล่อยว่างได้ • ถ้าพิมพ์ค่าใหม่ ระบบจะสร้างให้อัตโนมัติ
              </small>
            </div>
          ))}
        </div>

        {/* ราคา/สต็อก + บันทึก */}
        <div style={{ display: "flex", gap: 8, marginTop: 12, flexWrap: "wrap" }}>
          <input
            type="number"
            min={0}
            placeholder="ราคา"
            value={draft.price}
            onChange={(e) => setDraft((s) => ({ ...s, price: e.target.value }))}
            disabled={busy}
            style={{ maxWidth: 220 }}
          />
          <input
            type="number"
            min={0}
            placeholder="สต็อก"
            value={draft.stock_qty}
            onChange={(e) => setDraft((s) => ({ ...s, stock_qty: e.target.value }))}
            disabled={busy}
            style={{ maxWidth: 140 }}
          />
          <button onClick={createVariant} disabled={busy}>บันทึกแบบ/ตัวเลือก</button>
        </div>
      </section>

      {/* Generate จากทุกคอมโบ */}
      <section style={{ border: "1px solid #eee", borderRadius: 12, padding: 12 }}>
        <h3>สร้างแบบ/ตัวเลือกทั้งหมด (ทุกคอมบิเนชันที่มี)</h3>
        <div style={{ display: "flex", gap: 8, alignItems: "center", flexWrap: "wrap" }}>
          <label>SKU prefix</label>
          <input value={skuPrefix} onChange={(e) => setSkuPrefix(e.target.value)} disabled={busy} />
          <label>ราคาเริ่มต้น</label>
          <input type="number" min={0} value={genPrice} onChange={(e) => setGenPrice(e.target.value)} disabled={busy} style={{ maxWidth: 160 }} />
          <label>สต็อกเริ่มต้น</label>
          <input type="number" min={0} value={genStock} onChange={(e) => setGenStock(e.target.value)} disabled={busy} style={{ maxWidth: 160 }} />
          <button onClick={generateCombos} disabled={busy}>สร้างคอมบิเนชัน</button>
        </div>
        <div style={{ color: "#6b6b6b", marginTop: 6, fontSize: 14 }}>
          ระบบจะข้ามรายการที่ซ้ำ (ควรมี unique อย่างน้อยที่ sku หรือ (product_id, sku) ใน DB)
        </div>
      </section>

      {/* จัดการรายละเอียด (ลิสต์ออปชั่น) */}
      {showOptions && (
        <section style={{ border: "1px solid #eee", borderRadius: 12, padding: 12 }}>
          <h3>รายการรายละเอียด</h3>
          <div style={{ display: "flex", gap: 8, marginBottom: 12 }}>
            <input placeholder="ชื่อเรียก (ไม่จำเป็น เช่น สี/ขนาด/แบบซอง)" id="new-opt" disabled={busy}
              onKeyDown={(e) => { if (e.key === "Enter") addOption(e.currentTarget.value); }} />
            <button
              onClick={() => {
                const el = document.getElementById("new-opt");
                const v = (el?.value || "").trim();
                addOption(v);
                if (el) el.value = "";
              }}
              disabled={busy}
            >
              + เพิ่มรายละเอียด
            </button>
          </div>

          <div style={{ display: "grid", gap: 14 }}>
            {options.map((opt, idx) => (
              <div key={opt.option_id} style={{ borderTop: "1px dashed #ddd", paddingTop: 10 }}>
                <div style={{ display: "flex", alignItems: "center", gap: 8 }}>
                  <strong>{formLabel(opt, idx)}</strong>
                  <button
                    onClick={() => {
                      const name = prompt("ชื่อเรียกไว้ดูภายใน (ไม่แสดงให้ผู้ใช้)", opt.option_name || "");
                      if (name !== null) renameOption(opt.option_id, name.trim());
                    }}
                    disabled={busy}
                  >
                    ตั้งชื่อภายใน
                  </button>
                  <button onClick={() => deleteOption(opt.option_id)} disabled={busy} style={{ marginLeft: "auto" }}>
                    ลบรายละเอียด
                  </button>
                </div>

                {/* ค่าภายใต้รายละเอียดนี้ */}
                <div style={{ display: "flex", gap: 8, flexWrap: "wrap", marginTop: 8 }}>
                  {(opt.values || []).length ? (
                    opt.values.map((v) => (
                      <span key={v.value_id} style={{ background: "#f5f5f5", padding: "6px 10px", borderRadius: 8 }}>
                        {v.value_name}
                        <button onClick={() => deleteValue(v.value_id)} disabled={busy} style={{ marginLeft: 8 }}>x</button>
                      </span>
                    ))
                  ) : (
                    <span style={{ color: "#777" }}>— ยังไม่มีค่า —</span>
                  )}
                </div>

                {/* เพิ่มค่าใหม่แบบเร็ว */}
                <div style={{ display: "flex", gap: 8, marginTop: 8 }}>
                  <input placeholder="เพิ่มค่าใหม่ เช่น แบบซอง / 500 กรัม / สีแดง" id={`add-${opt.option_id}`} disabled={busy} />
                  <button
                    onClick={() => {
                      const el = document.getElementById(`add-${opt.option_id}`);
                      if (el && el.value.trim()) { addValue(opt.option_id, el.value.trim()); el.value = ""; }
                    }}
                    disabled={busy}
                  >
                    + เพิ่มค่า
                  </button>
                </div>
              </div>
            ))}
          </div>
        </section>
      )}

      {/* ตาราง Variants */}
      <section style={{ border: "1px solid #eee", borderRadius: 12, padding: 12 }}>
        <h3>รายการแบบ/ตัวเลือก (Variants)</h3>
        <VariantsTable
          rows={variants}
          comboText={comboText}
          onSave={(id, patch) => updateVariant(id, patch)}
          onDelete={(id) => deleteVariant(id)}
          busy={busy}
        />
      </section>
    </div>
  );
}

/* ตาราง variants */
function VariantsTable({ rows, comboText, onSave, onDelete, busy }) {
  return (
    <table width="100%" cellPadding="8" style={{ borderCollapse: "collapse" }}>
      <thead>
        <tr style={{ background: "#fafafa" }}>
          <th align="left">#</th>
          <th align="left">รูป</th>
          <th align="left">SKU</th>
          <th align="left">คอมโบ</th>
          <th align="right">ราคา</th>
          <th align="right">สต็อก</th>
          <th>สถานะ</th>
          <th>จัดการ</th>
        </tr>
      </thead>
      <tbody>
        {rows.length === 0 && (
          <tr>
            <td colSpan={8} align="center"><em>ยังไม่มี Variant</em></td>
          </tr>
        )}
        {rows.map((r, i) => (
          <VariantRow
            key={r.variant_id}
            idx={i + 1}
            row={r}
            comboText={comboText}
            onSave={onSave}
            onDelete={onDelete}
            busy={busy}
          />
        ))}
      </tbody>
    </table>
  );
}

function VariantRow({ idx, row, comboText, onSave, onDelete, busy }) {
  const [sku, setSku] = useState(row.sku || "");
  const [price, setPrice] = useState(row.final_price ?? row.price ?? "");
  const [stock, setStock] = useState(row.stock ?? row.stock_qty ?? 0);
  const [active, setActive] = useState(row.is_active ?? true);
  const [imageUrl, setImageUrl] = useState(row.image_url || "");
  const fileRef = useRef(null);

  return (
    <tr style={{ borderTop: "1px solid #eee" }}>
      <td>{idx}</td>
      <td>
        <div style={{ display: "flex", alignItems: "center", gap: 6 }}>
          <img
            src={imageUrl || ""}
            alt=""
            style={{ width: 48, height: 48, objectFit: "cover", borderRadius: 6, border: "1px solid #eee", display: imageUrl ? "block" : "none" }}
            onError={(e) => (e.currentTarget.style.display = "none")}
          />
        </div>
      </td>
      <td>
        <input value={sku} onChange={(e) => setSku(e.target.value)} style={{ width: 160 }} />
        <div style={{ display: "flex", gap: 6, marginTop: 6 }}>
          <input
            placeholder="URL รูป"
            value={imageUrl}
            onChange={(e) => setImageUrl(e.target.value)}
            style={{ width: 160 }}
          />
          <input
            type="file"
            accept="image/*"
            ref={fileRef}
            style={{ display: "none" }}
            onChange={async (e) => {
              const f = e.target.files?.[0];
              if (!f) return;
              try {
                const url = await tryUpload(f);
                if (url) setImageUrl(url);
              } catch {
                alert("อัปโหลดรูปไม่สำเร็จ (ถ้าไม่มี /api/uploads ให้ใส่ URL แทน)");
              } finally {
                if (fileRef.current) fileRef.current.value = "";
              }
            }}
          />
          <button type="button" onClick={() => fileRef.current?.click()} disabled={busy}>อัปโหลด…</button>
        </div>
      </td>
      <td>{comboText(row)}</td>
      <td align="right">
        <input type="number" min={0} value={price} onChange={(e) => setPrice(e.target.value)} style={{ width: 120, textAlign: "right" }} />
      </td>
      <td align="right">
        <input type="number" min={0} value={stock} onChange={(e) => setStock(e.target.value)} style={{ width: 100, textAlign: "right" }} />
      </td>
      <td>
        <label style={{ fontSize: 13 }}>
          <input type="checkbox" checked={!!active} onChange={(e) => setActive(e.target.checked)} /> แสดง
        </label>
      </td>
      <td>
        <button
          onClick={() => onSave(row.variant_id, { sku, price, stock_qty: stock, is_active: active, image_url: imageUrl })}
          disabled={busy}
        >
          บันทึก
        </button>
        <button onClick={() => onDelete(row.variant_id)} disabled={busy} style={{ marginLeft: 8 }}>
          ลบ
        </button>
      </td>
    </tr>
  );
}
