/* === ProductManagement.js (Part 1/4) START === */
// frontend/src/admin/ProductManagement.js
import React, { useCallback, useEffect, useRef, useState } from "react";
import { Link } from "react-router-dom";
import "./ProductManagement.css";
import { api, path } from "../lib/api";
import { useLookups } from "../lib/lookups";

/* ---------- Helpers ---------- */
const asStr = (v) => (v === null || v === undefined) ? "" : String(v);
const pickStatusName = (x) => x?.status_name ?? x?.StatusName ?? x?.name ?? "";
const toInt = (v) => {
  const n = parseInt(String(v ?? "").trim(), 10);
  return Number.isFinite(n) ? n : null;
};
const toNum = (v) => {
  if (v === null || v === undefined) return null;
  const n = Number(String(v).replace(/[,\sà¸¿]/g, ""));
  return Number.isFinite(n) ? n : null;
};

/* ---------- Tiny Toast ---------- */
function useToasts() {
  const [toasts, setToasts] = useState([]);
  const push = (msg, type = "info") => {
    const id = Math.random().toString(36).slice(2);
    setToasts((xs) => [...xs, { id, msg, type }]);
    setTimeout(() => setToasts((xs) => xs.filter((t) => t.id !== id)), 3000);
  };
  const remove = (id) => setToasts((xs) => xs.filter((t) => t.id !== id));
  return { toasts, push, remove };
}

/* ---------- Upload Helper (generic) ---------- */
async function uploadImage(file) {
  if (!file) return null;
  const fd = new FormData();
  fd.append("file", file);
  const res = await api.post(path("/upload"), fd, { headers: { "Content-Type": "multipart/form-data" } });
  return res?.data?.url || res?.data?.imageUrl || res?.data?.path || res?.data?.location || null;
}

/* ---------- Component ---------- */
export default function ProductManagement() {
  const [products, setProducts] = useState([]);
  const [statuses, setStatuses] = useState([]);
  const [loading, setLoading] = useState(false);
  const [showArchived, setShowArchived] = useState(false);

  /* filters (à¹€à¸œà¸·à¹ˆà¸­à¹ƒà¸Šà¹‰à¸•à¹ˆà¸­à¸¢à¸­à¸”à¸Ÿà¸´à¸¥à¹€à¸•à¸­à¸£à¹Œà¹€à¸”à¸´à¸¡à¸‚à¸­à¸‡à¸„à¸¸à¸“) */
  const [query, setQuery] = useState("");
  const [catFilter, setCatFilter] = useState("");
  const [subcatFilter, setSubcatFilter] = useState("");
  const [stockFilter, setStockFilter] = useState("all");

  /* lookups */
  const { data: lookups, reload: reloadLookups, loading: lkLoading } = useLookups({ published: true });

  /* form states */
  const [form, setForm] = useState({
    product_name: "", description: "", price: "",
    category_id: null, subcategory_id: null,
    product_unit_id: null, size_value: "", size_unit_id: null,
    origin: "", product_status_id: ""
  });
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [previews, setPreviews] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [editProductId, setEditProductId] = useState(null);

  /* variant panel state (à¸£à¸§à¸¡ VariantsManager à¹ƒà¸™à¸«à¸™à¹‰à¸²à¸™à¸µà¹‰) */
  const [showVariantPanel, setShowVariantPanel] = useState(false);
  const [opt1Name, setOpt1Name] = useState("à¸ªà¸µ");
  const [opt2Name, setOpt2Name] = useState("à¸‚à¸™à¸²à¸”");
  const [opt3Name, setOpt3Name] = useState("");
  const [opt1Values, setOpt1Values] = useState([]);
  const [opt2Values, setOpt2Values] = useState([]);
  const [opt3Values, setOpt3Values] = useState([]);
  const [variantRows, setVariantRows] = useState([]);
  const [savingVariants, setSavingVariants] = useState(false);

  /* refs */
  const nameRef = useRef(null);
  const formTopRef = useRef(null);

  /* toasts */
  const { toasts, push, remove } = useToasts();

  /* ---------- API Helpers ---------- */
  const handleApiError = (err, fallback = "à¹€à¸à¸´à¸”à¸‚à¹‰à¸­à¸œà¸´à¸”à¸žà¸¥à¸²à¸”") => {
    console.error(fallback, err?.response?.data || err?.message || err);
    const msg = err?.response?.data?.message || err?.message || fallback;
    push(`âŒ ${msg}`, "danger");
  };

  /* ---------- Load Products ---------- */
  const fetchProducts = useCallback(async () => {
    try {
      const { data } = await api.get(path("/admin/products"), {
        params: { include_archived: showArchived ? 1 : 0 },
      });
      let items = Array.isArray(data?.items) ? data.items : (Array.isArray(data) ? data : []);
      items = items.map((p) => {
        const stock = Number(p.stock_qty ?? p.stock ?? 0);
        return {
          ...p,
          product_status_name: stock <= 0 ? "à¸ªà¸´à¸™à¸„à¹‰à¸²à¸«à¸¡à¸”" : stock <= 5 ? "à¸ªà¸•à¹‡à¸­à¸à¹ƒà¸à¸¥à¹‰à¸«à¸¡à¸”" : "à¸žà¸£à¹‰à¸­à¸¡à¸ˆà¸³à¸«à¸™à¹ˆà¸²à¸¢",
        };
      });
      setProducts(items);
    } catch (err) {
      handleApiError(err, "âŒ à¹‚à¸«à¸¥à¸”à¸ªà¸´à¸™à¸„à¹‰à¸²à¹„à¸¡à¹ˆà¸ªà¸³à¹€à¸£à¹‡à¸ˆ");
    }
  }, [showArchived]);

  /* ---------- Load Statuses ---------- */
  const fetchStatuses = useCallback(async () => {
    try {
      let res = await api.get(path("/product-status"));
      if (!Array.isArray(res.data)) { try { res = await api.get(path("/product-statuses")); } catch {} }
      const normalized = (res.data || []).map((x) => ({
        id: asStr(x?.product_status_id ?? x?.id),
        name: pickStatusName(x),
      }));
      setStatuses(normalized);
    } catch {
      console.warn("âš  à¹„à¸¡à¹ˆà¸¡à¸µ endpoint product-status");
      setStatuses([]);
    }
  }, []);

  /* ---------- Toggle publish/archive ---------- */
  const togglePublish = useCallback(async (productId, current) => {
    setProducts((prev) => prev.map((p) => p.product_id === productId ? { ...p, is_published: !current } : p));
    try {
      await api.patch(path(`/admin/products/${productId}/publish`), { is_published: !current });
      push(!current ? "âœ… à¹€à¸›à¸´à¸”à¹à¸ªà¸”à¸‡à¸ªà¸´à¸™à¸„à¹‰à¸²à¹à¸¥à¹‰à¸§" : "âœ… à¸‹à¹ˆà¸­à¸™à¸ªà¸´à¸™à¸„à¹‰à¸²à¹à¸¥à¹‰à¸§", "ok");
      await fetchProducts();
    } catch (err) {
      handleApiError(err, "âŒ à¸­à¸±à¸›à¹€à¸”à¸•à¸ªà¸–à¸²à¸™à¸°à¹„à¸¡à¹ˆà¸ªà¸³à¹€à¸£à¹‡à¸ˆ");
    }
  }, [fetchProducts]);

  const archiveProduct = useCallback(async (id) => {
    if (!window.confirm("à¸•à¹‰à¸­à¸‡à¸à¸²à¸£à¹€à¸à¹‡à¸šà¸ªà¸´à¸™à¸„à¹‰à¸²à¸™à¸µà¹‰à¹„à¸§à¹‰à¹ƒà¸™à¸„à¸¥à¸±à¸‡à¹ƒà¸Šà¹ˆà¹„à¸«à¸¡?")) return;
    try {
      await api.delete(path(`/admin/products/${id}`));
      push("ðŸ—ƒï¸ à¸¢à¹‰à¸²à¸¢à¹„à¸›à¸–à¸±à¸‡à¹€à¸à¹‡à¸šà¹à¸¥à¹‰à¸§", "warn");
      await fetchProducts();
    } catch (err) {
      handleApiError(err, "âŒ à¹€à¸à¹‡à¸šà¸ªà¸´à¸™à¸„à¹‰à¸²à¹„à¸¡à¹ˆà¸ªà¸³à¹€à¸£à¹‡à¸ˆ");
    }
  }, [fetchProducts]);

  const unarchiveProduct = useCallback(async (id) => {
    try {
      await api.patch(path(`/admin/products/${id}/unarchive`));
      push("â™»ï¸ à¸à¸¹à¹‰à¸„à¸·à¸™à¸ªà¸´à¸™à¸„à¹‰à¸²à¹à¸¥à¹‰à¸§", "ok");
      await fetchProducts();
    } catch (err) {
      handleApiError(err, "âŒ à¸à¸¹à¹‰à¸„à¸·à¸™à¸ªà¸´à¸™à¸„à¹‰à¸²à¹„à¸¡à¹ˆà¸ªà¸³à¹€à¸£à¹‡à¸ˆ");
    }
  }, [fetchProducts]);

  /* ---------- Init ---------- */
  useEffect(() => {
    (async () => {
      setLoading(true);
      await Promise.all([fetchProducts(), fetchStatuses(), reloadLookups()]);
      setLoading(false);
    })();
  }, [fetchProducts, fetchStatuses, reloadLookups, showArchived]);
/* === ProductManagement.js (Part 1/4) END === */
/* === ProductManagement.js (Part 2/4) START === */
  /* ---------- Form Events ---------- */
  const onChange = (e) => {
    const { name, value } = e.target;
    setForm((p) => ({ ...p, [name]: value }));
  };

  const onFiles = (e) => setSelectedFiles(Array.from(e.target.files || []));
  useEffect(() => {
    if (!selectedFiles.length) { setPreviews([]); return; }
    const urls = selectedFiles.map((f) => ({ name: f.name, url: URL.createObjectURL(f) }));
    setPreviews(urls);
    return () => urls.forEach((u) => URL.revokeObjectURL(u.url));
  }, [selectedFiles]);

  const clearForm = () => {
    setForm({
      product_name: "", description: "", price: "",
      category_id: null, subcategory_id: null,
      product_unit_id: null, size_value: "", size_unit_id: null,
      origin: "", product_status_id: ""
    });
    setSelectedFiles([]); setPreviews([]);
    setIsEditing(false); setEditProductId(null);
  };

  /* ---------- Submit ---------- */
  const onSubmit = async (e) => {
    e.preventDefault();
    const price = parseInt(form.price);
    if (isNaN(price) || price < 0) { push("âš ï¸ à¸£à¸²à¸„à¸²à¸•à¹‰à¸­à¸‡à¹€à¸›à¹‡à¸™à¸ˆà¸³à¸™à¸§à¸™à¹€à¸•à¹‡à¸¡", "warn"); return; }
    try {
      let uploaded = [];
      if (selectedFiles.length > 0)
        uploaded = (await Promise.all(selectedFiles.map(uploadImage))).filter(Boolean);

      const body = {
        product_name: form.product_name.trim(),
        description: form.description,
        price,
        category_id: form.category_id,
        subcategory_id: form.subcategory_id,
        product_unit_id: form.product_unit_id,
        size_unit_id: form.size_unit_id,
        size_value: form.size_value,
        origin: form.origin,
        product_status_id: form.product_status_id,
      };

      let id = editProductId;
      if (isEditing && id) {
        await api.put(path(`/admin/products/${id}`), body);
        push("âœ… à¸­à¸±à¸›à¹€à¸”à¸•à¸ªà¸´à¸™à¸„à¹‰à¸²à¸ªà¸³à¹€à¸£à¹‡à¸ˆ", "ok");
      } else {
        const res = await api.post(path("/admin/products"), body);
        id = res?.data?.product_id ?? res?.data?.id;
        push("ðŸŽ‰ à¹€à¸žà¸´à¹ˆà¸¡à¸ªà¸´à¸™à¸„à¹‰à¸²à¹€à¸£à¸µà¸¢à¸šà¸£à¹‰à¸­à¸¢", "ok");
      }

      if (uploaded.length) {
        for (let i = 0; i < uploaded.length; i++) {
          try {
            await api.post(path(`/admin/products/${id}/images`), {
              images: [{ url: uploaded[i], position: i + 1, is_primary: i === 0 }],
            });
          } catch {}
        }
      }

      await fetchProducts();
      await reloadLookups();
      clearForm();
    } catch (err) {
      handleApiError(err, "âŒ à¸šà¸±à¸™à¸—à¸¶à¸à¸ªà¸´à¸™à¸„à¹‰à¸²à¹„à¸¡à¹ˆà¸ªà¸³à¹€à¸£à¹‡à¸ˆ");
    }
  };

  /* ---------- Variants Panel Handlers ---------- */
  const addTag = (list, setList, txt) => {
    const v = String(txt || "").trim();
    if (!v) return;
    if (!list.includes(v)) setList([...list, v]);
  };
  const removeTag = (list, setList, idx) => {
    const arr = list.filter((_, i) => i !== idx);
    setList(arr);
    push("à¸¥à¸šà¸„à¹ˆà¸²à¹€à¸£à¸µà¸¢à¸šà¸£à¹‰à¸­à¸¢", "warn");
  };
  const removeOption = (opt) => {
    if (opt === 1) { setOpt1Name(""); setOpt1Values([]); }
    if (opt === 2) { setOpt2Name(""); setOpt2Values([]); }
    if (opt === 3) { setOpt3Name(""); setOpt3Values([]); }
    push("à¸¥à¸šà¸•à¸±à¸§à¹€à¸¥à¸·à¸­à¸à¸™à¸µà¹‰à¹à¸¥à¹‰à¸§", "warn");
  };
  const resetAllOptions = () => {
    setOpt1Name("à¸ªà¸µ"); setOpt2Name("à¸‚à¸™à¸²à¸”"); setOpt3Name("");
    setOpt1Values([]); setOpt2Values([]); setOpt3Values([]);
    setVariantRows([]);
    push("à¸¥à¹‰à¸²à¸‡à¸•à¸±à¸§à¹€à¸¥à¸·à¸­à¸à¸—à¸±à¹‰à¸‡à¸«à¸¡à¸”à¹à¸¥à¹‰à¸§", "warn");
  };

  /* ---------- Build Variant Rows (combinations) ---------- */
  useEffect(() => {
    const v1 = opt1Values.length ? opt1Values : [null];
    const v2 = opt2Values.length ? opt2Values : [null];
    const v3 = opt3Values.length ? opt3Values : [null];
    const combos = [];
    for (const a of v1) for (const b of v2) for (const c of v3)
      combos.push({ opt1: a, opt2: b, opt3: c, price: "", sku: "", image: null, variant_id: null });
    setVariantRows(combos);
  }, [opt1Values, opt2Values, opt3Values]);

  /* ---------- Upload per row ---------- */
  const onUploadRowImage = async (file, idx) => {
    if (!file) return;
    const temp = URL.createObjectURL(file);
    setVariantRows((prev) => { const a = [...prev]; a[idx] = { ...a[idx], image: temp }; return a; });
    try {
      const url = await uploadImage(file);
      setVariantRows((prev) => { const a = [...prev]; a[idx] = { ...a[idx], image: url }; return a; });
    } catch (err) { handleApiError(err, "à¸­à¸±à¸›à¹‚à¸«à¸¥à¸”à¸£à¸¹à¸›à¹„à¸¡à¹ˆà¸ªà¸³à¹€à¸£à¹‡à¸ˆ"); }
    finally { setTimeout(() => URL.revokeObjectURL(temp), 2500); }
  };

  /* ---------- Delete Variant Row ---------- */
  const handleDeleteVariant = async (row, idx) => {
    if (!row.variant_id) {
      setVariantRows((p) => p.filter((_, i) => i !== idx));
      push("ðŸ—‘ï¸ à¸¥à¸šà¸•à¸±à¸§à¹€à¸¥à¸·à¸­à¸à¸­à¸­à¸à¸ˆà¸²à¸à¸£à¸²à¸¢à¸à¸²à¸£à¹à¸¥à¹‰à¸§", "warn");
      return;
    }
