      <div className="grid">
        {items.map((p) => (
          <div key={p.id} className="card">
            <img
              src={mediaSrc(p.image)} // ‚úÖ ‡πÉ‡∏ä‡πâ mediaSrc ‡πÄ‡∏û‡∏∑‡πà‡∏≠‡πÉ‡∏´‡πâ‡∏£‡∏π‡∏õ‡∏Ç‡∏∂‡πâ‡∏ô‡πÅ‡∏ô‡πà‡∏ô‡∏≠‡∏ô
              alt={p.name}
              onError={(e) => (e.target.src = "/no-image.png")}
            />
            <div className="info">
              <h3>{p.name}</h3>
              <p>
                ‡∏£‡∏≤‡∏Ñ‡∏≤:{" "}
                {p.priceMin === p.priceMax
                  ? `‡∏ø${p.priceMin ?? "-"}`
                  : `‡∏ø${p.priceMin} - ‡∏ø${p.priceMax}`}
                <br />
                ‡∏™‡∏ï‡πá‡∏≠‡∏Å: {p.totalStock}
