              <Link to={`/admin/products/detail/${p.id}`} className="btn">
                ‡∏î‡∏π‡∏£‡∏≤‡∏¢‡∏•‡∏∞‡πÄ‡∏≠‡∏µ‡∏¢‡∏î
              </Link>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
