import React, { useEffect, useMemo, useState } from "react";
import {
  listInventory,
  receiveInventory,
  issueInventory,
  listMoves,
} from "../lib/api";
import VariantPicker from "../components/VariantPicker";

/* utils */
const fmt = (n) => new Intl.NumberFormat("th-TH").format(Number(n || 0));
const currency = (n) =>
  new Intl.NumberFormat("th-TH", { style: "currency", currency: "THB" }).format(
    Number(n || 0)
  );
const cls = (...xs) => xs.filter(Boolean).join(" ");

export default function InventoryPage() {
  const [tab, setTab] = useState("overview"); // overview | receive | issue | moves
  return (
    <div className="p-4 max-w-6xl mx-auto">
      <h1 className="text-2xl font-bold">Inventory Management</h1>

      {/* Tabs */}
      <div className="mt-4 flex gap-2">
        {[
          ["overview", "ภาพรวมคงคลัง"],
          ["receive", "รับเข้า (IN)"],
          ["issue", "ตัดออก (OUT) FIFO"],
          ["moves", "ประวัติ (Moves)"],
        ].map(([k, label]) => (
          <button
            key={k}
            className={cls(
              "px-3 py-2 rounded border",
              tab === k ? "bg-black text-white border-black" : "bg-white"
            )}
            onClick={() => setTab(k)}
          >
            {label}
          </button>
        ))}
      </div>

      <div className="mt-6">
        {tab === "overview" && <InventoryOverview />}
        {tab === "receive" && <ReceiveForm />}
        {tab === "issue" && <IssueForm />}
        {tab === "moves" && <MovesPanel />}
      </div>
    </div>
  );
}

/* ---------- 1) ภาพรวม ---------- */
function InventoryOverview() {
  const [q, setQ] = useState("");
  const [scope, setScope] = useState("variant");
  const [order, setOrder] = useState("low_stock");
  const [page, setPage] = useState(1);
  const [pageSize] = useState(20);
  const [items, setItems] = useState([]);
  const [total, setTotal] = useState(0);
  const [loading, setLoading] = useState(false);

  const offset = (page - 1) * pageSize;
  const totalPages = Math.max(1, Math.ceil(total / pageSize));

  async function load() {
    setLoading(true);
    try {
      const data = await listInventory({
        search: q,
        scope,
        order,
        limit: pageSize,
        offset,
      });
      setItems(data.items || []);
      setTotal(Number(data.total || 0));
    } catch (e) {
      console.error(e);
      alert(e?.response?.data?.error || "โหลดข้อมูลไม่สำเร็จ");
    } finally {
      setLoading(false);
    }
  }

  useEffect(() => { load(); /* eslint-disable-next-line */ }, [scope, order, page]);

  return (
    <div>
      <div className="flex flex-wrap items-end gap-2">
        <div className="min-w-[260px]">
          <label className="block text-sm">ค้นหา</label>
          <input
            value={q}
            onChange={(e) => setQ(e.target.value)}
            placeholder="ชื่อสินค้า / คำอธิบาย / SKU"
            className="border rounded px-3 py-2 w-full"
          />
        </div>
        <div>
          <label className="block text-sm">Scope</label>
          <select
            value={scope}
            onChange={(e) => { setScope(e.target.value); setPage(1); }}
            className="border rounded px-3 py-2"
          >
            <option value="variant">รายตัวเลือก (variant)</option>
            <option value="product">รายสินค้า (product)</option>
          </select>
        </div>
        <div>
          <label className="block text-sm">จัดเรียง</label>
          <select
            value={order}
            onChange={(e) => setOrder(e.target.value)}
            className="border rounded px-3 py-2"
          >
            <option value="low_stock">เหลือน้อยก่อน</option>
            <option value="newest">ใหม่ล่าสุด</option>
            <option value="name_asc">ชื่อ A→Z</option>
            <option value="name_desc">ชื่อ Z→A</option>
          </select>
        </div>
        <button
          onClick={() => { setPage(1); load(); }}
          className="px-3 py-2 border rounded bg-black text-white"
        >
          ค้นหา
        </button>
      </div>

      <div className="mt-4 overflow-auto border rounded">
        <table className="min-w-[720px] w-full">
          <thead className="bg-gray-100">
            <tr>
              <th className="text-left p-2">สินค้า</th>
              {scope === "variant" && <th className="text-left p-2">SKU</th>}
              <th className="text-right p-2">สต๊อก</th>
              {scope === "variant" && <th className="text-right p-2">ราคา</th>}
            </tr>
          </thead>
          <tbody>
            {loading ? (
              <tr><td className="p-4" colSpan={scope === "variant" ? 4 : 2}>กำลังโหลด...</td></tr>
            ) : items.length === 0 ? (
              <tr><td className="p-4" colSpan={scope === "variant" ? 4 : 2}>ไม่พบข้อมูล</td></tr>
            ) : items.map((it) => (
              <tr key={`${it.product_id}-${it.variant_id || "p"}`} className="border-t">
                <td className="p-2"><div className="font-medium">{it.product_name || `#${it.product_id}`}</div></td>
                {scope === "variant" && <td className="p-2">{it.sku || "-"}</td>}
                <td className="p-2 text-right">{fmt(it.stock)}</td>
                {scope === "variant" && <td className="p-2 text-right">{it.price ? currency(it.price) : "-"}</td>}
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div className="mt-3 flex items-center gap-2">
        <span>ทั้งหมด {fmt(total)} รายการ • หน้า {page}/{totalPages}</span>
        <div className="ml-auto flex gap-1">
          <button disabled={page <= 1}
            onClick={() => setPage((p) => Math.max(1, p - 1))}
            className="px-3 py-2 border rounded disabled:opacity-50">◀</button>
          <button disabled={page >= totalPages}
            onClick={() => setPage((p) => Math.min(totalPages, p + 1))}
            className="px-3 py-2 border rounded disabled:opacity-50">▶</button>
        </div>
      </div>
    </div>
  );
}

/* ---------- 2) รับเข้า (IN) ---------- */
function ReceiveForm() {
  const [variantId, setVariantId] = useState("");
  const [selected, setSelected] = useState(null);
  const [qty, setQty] = useState("");
  const [unitCost, setUnitCost] = useState("");
  const [note, setNote] = useState("");
  const [resJson, setResJson] = useState(null);
  const [loading, setLoading] = useState(false);

  async function onSubmit(e) {
    e.preventDefault();
    if (!variantId || !qty || !unitCost) {
      alert("กรอก variant_id, qty, unit_cost ให้ครบ");
      return;
    }
    setLoading(true);
    try {
      const data = await receiveInventory({
        variant_id: Number(variantId),
        qty: Number(qty),
        unit_cost: Number(unitCost),
        note: note || null,
      });
      setResJson(data);
      alert(`รับเข้าเรียบร้อย (lot_id=${data?.lot?.lot_id})`);
    } catch (e) {
      console.error(e);
      alert(e?.response?.data?.error || "รับเข้าไม่สำเร็จ");
    } finally {
      setLoading(false);
    }
  }

  return (
    <form onSubmit={onSubmit} className="grid gap-3 max-w-xl">
      {/* 🟢 ตัวเลือกค้นหาง่าย */}
      <VariantPicker
        onChange={(it) => { setVariantId(it.variant_id); setSelected(it); }}
        autoFocus
      />

      <div className="grid sm:grid-cols-2 gap-3">
        <div>
          <label className="block text-sm">Variant ID</label>
          <input
            value={variantId}
            onChange={(e) => setVariantId(e.target.value)}
            className="border rounded px-3 py-2 w-full"
            type="number"
            min="1"
          />
          {selected && (
            <div className="text-xs text-gray-600 mt-1">
              {selected.product_name} • SKU: {selected.sku || "-"} • คงคลัง: {fmt(selected.stock)}
            </div>
          )}
        </div>
        <div>
          <label className="block text-sm">จำนวน (qty)</label>
          <input
            value={qty}
            onChange={(e) => setQty(e.target.value)}
            className="border rounded px-3 py-2 w-full"
            type="number"
            min="1"
          />
        </div>
        <div>
          <label className="block text-sm">ต้นทุนต่อหน่วย (unit_cost)</label>
          <input
            value={unitCost}
            onChange={(e) => setUnitCost(e.target.value)}
            className="border rounded px-3 py-2 w-full"
            type="number"
            step="0.01"
            min="0"
          />
        </div>
        <div>
          <label className="block text-sm">หมายเหตุ</label>
          <input
            value={note}
            onChange={(e) => setNote(e.target.value)}
            className="border rounded px-3 py-2 w-full"
            placeholder="เช่น รับเข้าล็อตใหม่"
          />
        </div>
      </div>

      <button type="submit" disabled={loading}
        className="px-4 py-2 rounded bg-green-600 text-white disabled:opacity-50">
        {loading ? "กำลังบันทึก..." : "บันทึกการรับเข้า (IN)"}
      </button>

      {resJson && (
        <pre className="bg-gray-50 border rounded p-3 text-xs overflow-auto">
          {JSON.stringify(resJson, null, 2)}
        </pre>
      )}
    </form>
  );
}

/* ---------- 3) ตัดออก (OUT) FIFO ---------- */
function IssueForm() {
  const [variantId, setVariantId] = useState("");
  const [selected, setSelected] = useState(null);
  const [qty, setQty] = useState("");
  const [note, setNote] = useState("");
  const [ref, setRef] = useState("");
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  async function onSubmit(e) {
    e.preventDefault();
    if (!variantId || !qty) {
      alert("กรอก variant_id และ qty");
      return;
    }
    setLoading(true);
    try {
      const data = await issueInventory({
        variant_id: Number(variantId),
        qty: Number(qty),
        note: note || null,
        ref_order_detail_id: ref ? Number(ref) : null,
      });
      setResult(data);
      alert("ตัดสต๊อกเรียบร้อย");
    } catch (e) {
      console.error(e);
      alert(e?.response?.data?.error || "ตัดสต๊อกไม่สำเร็จ");
    } finally {
      setLoading(false);
    }
  }

  const totalAllocated = useMemo(
    () => (result?.allocations || []).reduce((s, a) => s + Number(a.allocated_qty || 0), 0),
    [result]
  );

  return (
    <div className="grid gap-4">
      <form onSubmit={onSubmit} className="grid gap-3 max-w-xl">
        {/* 🟢 ตัวเลือกค้นหาง่าย */}
        <VariantPicker
          onChange={(it) => { setVariantId(it.variant_id); setSelected(it); }}
        />

        <div className="grid sm:grid-cols-2 gap-3">
          <div>
            <label className="block text-sm">Variant ID</label>
            <input
              value={variantId}
              onChange={(e) => setVariantId(e.target.value)}
              className="border rounded px-3 py-2 w-full"
              type="number"
              min="1"
            />
            {selected && (
              <div className="text-xs text-gray-600 mt-1">
                {selected.product_name} • SKU: {selected.sku || "-"} • คงคลัง: {fmt(selected.stock)}
              </div>
            )}
          </div>
          <div>
            <label className="block text-sm">จำนวน (qty)</label>
            <input
              value={qty}
              onChange={(e) => setQty(e.target.value)}
              className="border rounded px-3 py-2 w-full"
              type="number"
              min="1"
            />
          </div>
          <div>
            <label className="block text-sm">หมายเหตุ</label>
            <input
              value={note}
              onChange={(e) => setNote(e.target.value)}
              className="border rounded px-3 py-2 w-full"
              placeholder="เช่น ตัดสต๊อกออเดอร์ #555"
            />
          </div>
          <div>
            <label className="block text-sm">อ้างอิง (ref_order_detail_id)</label>
            <input
              value={ref}
              onChange={(e) => setRef(e.target.value)}
              className="border rounded px-3 py-2 w-full"
              type="number"
              min="1"
              placeholder="ระบุถ้ามี"
            />
          </div>
        </div>

        <button type="submit" disabled={loading}
          className="px-4 py-2 rounded bg-red-600 text-white disabled:opacity-50">
          {loading ? "กำลังตัดสต๊อก..." : "บันทึกการตัดสต๊อก (OUT) FIFO"}
        </button>
      </form>

      {result?.allocations?.length > 0 && (
        <div className="border rounded">
          <div className="p-2 font-medium bg-gray-50 border-b">
            Allocations (รวม {fmt(totalAllocated)} หน่วย)
          </div>
          <table className="w-full">
            <thead className="bg-gray-100">
              <tr>
                <th className="text-left p-2">Lot ID</th>
                <th className="text-right p-2">ตัดออก</th>
                <th className="text-right p-2">ต้นทุน/หน่วย</th>
                <th className="text-right p-2">Move ID</th>
                <th className="text-left p-2">เวลา</th>
              </tr>
            </thead>
            <tbody>
              {result.allocations.map((a) => (
                <tr key={a.move_id} className="border-t">
                  <td className="p-2">{a.lot_id}</td>
                  <td className="p-2 text-right">{fmt(a.allocated_qty)}</td>
                  <td className="p-2 text-right">{currency(a.unit_cost)}</td>
                  <td className="p-2 text-right">{a.move_id}</td>
                  <td className="p-2">{new Date(a.move_at).toLocaleString()}</td>
                </tr>
              ))}
            </tbody>
          </table>
          <pre className="bg-gray-50 border-t rounded-b p-3 text-xs overflow-auto">
            {JSON.stringify(result, null, 2)}
          </pre>
        </div>
      )}
    </div>
  );
}

/* ---------- 4) Moves ---------- */
function MovesPanel() {
  const [variantId, setVariantId] = useState("");
  const [type, setType] = useState("");
  const [from, setFrom] = useState("");
  const [to, setTo] = useState("");
  const [limit, setLimit] = useState(200);
  const [rows, setRows] = useState([]);
  const [loading, setLoading] = useState(false);

  async function load() {
    setLoading(true);
    try {
      const data = await listMoves({
        variant_id: variantId || undefined,
        type: type || undefined,
        from: from || undefined,
        to: to || undefined,
        limit,
      });
      setRows(data || []);
    } catch (e) {
      console.error(e);
      alert(e?.response?.data?.error || "โหลดประวัติไม่สำเร็จ");
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="grid gap-3">
      <div className="grid sm:grid-cols-5 gap-3">
        <div className="sm:col-span-2">
          {/* ใช้ picker ช่วยกรอง variant ได้ด้วย */}
          <VariantPicker onChange={(it) => setVariantId(it.variant_id)} />
          <input
            value={variantId}
            onChange={(e) => setVariantId(e.target.value)}
            className="border rounded px-3 py-2 w-full mt-2"
            type="number"
            min="1"
            placeholder="หรือกรอก Variant ID ตรงๆ"
          />
        </div>
        <div>
          <label className="block text-sm">Type</label>
          <select
            value={type}
            onChange={(e) => setType(e.target.value)}
            className="border rounded px-3 py-2 w-full"
          >
            <option value="">ทั้งหมด</option>
            <option value="IN">IN</option>
            <option value="OUT">OUT</option>
            <option value="ADJ">ADJ</option>
          </select>
        </div>
        <div>
          <label className="block text-sm">จากวันที่</label>
          <input
            type="datetime-local"
            value={from}
            onChange={(e) => setFrom(e.target.value)}
            className="border rounded px-3 py-2 w-full"
          />
        </div>
        <div>
          <label className="block text-sm">ถึงวันที่</label>
          <input
            type="datetime-local"
            value={to}
            onChange={(e) => setTo(e.target.value)}
            className="border rounded px-3 py-2 w-full"
          />
        </div>
      </div>

      <div>
        <button onClick={load} className="px-3 py-2 border rounded bg-black text-white">
          โหลดประวัติ
        </button>
      </div>

      <div className="overflow-auto border rounded">
        <table className="min-w-[760px] w-full">
          <thead className="bg-gray-100">
            <tr>
              <th className="text-left p-2">เวลา</th>
              <th className="text-left p-2">ประเภท</th>
              <th className="text-right p-2">จำนวน</th>
              <th className="text-left p-2">Lot</th>
              <th className="text-left p-2">Variant</th>
              <th className="text-left p-2">Note</th>
            </tr>
          </thead>
          <tbody>
            {loading ? (
              <tr><td className="p-3" colSpan={6}>กำลังโหลด...</td></tr>
            ) : rows.length === 0 ? (
              <tr><td className="p-3" colSpan={6}>ไม่มีข้อมูล</td></tr>
            ) : (
              rows.map((r) => (
                <tr key={r.move_id} className="border-t">
                  <td className="p-2">{new Date(r.created_at).toLocaleString()}</td>
                  <td className="p-2">{r.move_type}</td>
                  <td className="p-2 text-right">{fmt(r.change_qty)}</td>
                  <td className="p-2">{r.lot_id || "-"}</td>
                  <td className="p-2">{r.product_variant_id}</td>
                  <td className="p-2">{r.note || "-"}</td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}
